import React, { useState } from 'react';

const PautaItem = ({ pauta, setVotos }) => {
  const [voto, setVoto] = useState(null);

  const votar = (opcao) => {
    if (voto === opcao) return; // Evita reselecionar a mesma opção

    setVoto(opcao);
    setVotos(prev => [...prev, { titulo: pauta.titulo, opcao }]);
  };

  return (
    <div className="pauta-item">
      <h3>{pauta.titulo}</h3>
      <label>
        <input type="checkbox" checked={voto === 'sim'} onChange={() => votar('sim')} />
        ✅ Sim
      </label>
      <label>
        <input type="checkbox" checked={voto === 'nao'} onChange={() => votar('nao')} />
        ❌ Não
      </label>
    </div>
  );
};

export default PautaItem;
