import React, { useState, useEffect } from 'react';
import api from '../Services/api';
import Pauta from '../Components/pauta';

const Home = () => {
  const [pautas, setPautas] = useState([]);
  const [cpf, setCpf] = useState('');
  const [mensagem, setMensagem] = useState('');

  useEffect(() => {
    api.get('/pautas')
      .then(response => setPautas(response.data))
      .catch(error => console.error("Erro ao buscar pautas", error));
  }, []);

  const votar = async (id, voto) => {
    if (!cpf) {
      setMensagem("Por favor, insira um CPF.");
      return;
    }

    try {
      const response = await api.post(`/voto/${id}`, { cpf, voto });
      setMensagem(response.data.mensagem);
    } catch (error) {
      setMensagem(error.response?.data?.erro || "Erro ao registrar voto");
    }
  };

  return (
    <div>
      <h1>Pautas</h1>
      <input type="text" placeholder="Digite seu CPF" value={cpf} onChange={e => setCpf(e.target.value)} />
      {mensagem && <p>{mensagem}</p>}
      {pautas.length > 0 ? (
        pautas.map(pauta => (
          <div key={pauta._id}>
            <Pauta titulo={pauta.titulo} votos={pauta.votos} />
            <button onClick={() => votar(pauta._id, 'sim')}>Votar SIM</button>
            <button onClick={() => votar(pauta._id, 'nao')}>Votar N√ÉO</button>
          </div>
        ))
      ) : (
        <p>Nenhuma pauta encontrada.</p>
      )}
    </div>
  );
};

export default Home;
