import React, { useState, useEffect } from 'react';
import api from '../Services/api';

const Home = () => {
  const [pautas, setPautas] = useState([]);
  const [cpf, setCpf] = useState('');
  const [mensagem, setMensagem] = useState('');

  useEffect(() => {
    api.get('/pautas')
      .then(response => setPautas(response.data))
      .catch(error => console.error("Erro ao buscar pautas", error));
  }, []);

  const votar = async (id, voto) => {
    try {
      const response = await api.post(`/pautas/${id}/voto`, { cpf, voto });
      setMensagem(response.data.mensagem);
    } catch (error) {
      setMensagem(error.response?.data?.erro || "Erro ao registrar voto");
    }
  };

  const formatarCPF = (valor) => {
    let cpf = valor.replace(/\D/g, ''); 

    if (cpf.length > 11) {
      cpf = cpf.slice(0, 11); 
    }

    return cpf
      .replace(/(\d{3})(\d)/, '$1.$2')
      .replace(/(\d{3})(\d)/, '$1.$2')
      .replace(/(\d{3})(\d{1,2})$/, '$1-$2');
  };

  return (
    <div>
      <h1>Pautas</h1>
      <input
        type="text"
        placeholder="Digite seu CPF"
        value={cpf}
        onChange={(e) => setCpf(formatarCPF(e.target.value))}
        maxLength={14} 
      />
      {mensagem && <p>{mensagem}</p>}
      {pautas.length > 0 ? (
        pautas.map(pauta => (
          <div key={pauta.id}>
            <h2>{pauta.titulo}</h2>
            <p>Votos SIM: {pauta.votos_sim} | NÃO: {pauta.votos_nao}</p>
            <button onClick={() => votar(pauta.id, 'sim')}>Votar SIM</button>
            <button onClick={() => votar(pauta.id, 'nao')}>Votar NÃO</button>
          </div>
        ))
      ) : (
        <p>Nenhuma pauta encontrada.</p>
      )}
    </div>
  );
};

export default Home;
