import React, { useState, useEffect } from 'react';
import api from '../Services/api';
import PautaItem from '../Components/pautaItem';

const Home = () => {
  const [cpf, setCpf] = useState('');
  const [apto, setApto] = useState(null);
  const [pautas, setPautas] = useState([]);
  const [votos, setVotos] = useState([]);

  const formatarCPF = (valor) => {
    let cpf = valor.replace(/\D/g, '').slice(0, 11);
    return cpf
      .replace(/(\d{3})(\d)/, '$1.$2')
      .replace(/(\d{3})(\d)/, '$1.$2')
      .replace(/(\d{3})(\d{1,2})$/, '$1-$2');
  };

  const validarCPF = async (valor) => {
    const cpfFormatado = formatarCPF(valor);
    setCpf(cpfFormatado);

    try {
      const response = await api.get(`/users/validate/${valor.replace(/\D/g, '')}`);
      setApto(response.data.mensagem.includes('apto'));
    } catch {
      setApto(false);
    }
  };

  useEffect(() => {
    api.get('/polls') 
      .then(response => setPautas(response.data.slice(0, 10))) 
      .catch(error => console.error("❌ Erro ao buscar votações:", error));
  }, []);
  

  return (
    <div>
        <h1>Votação Online</h1>

        <input 
          type="text" 
          placeholder="Digite seu CPF" 
          value={cpf} 
          onChange={(e) => validarCPF(e.target.value)}
          maxLength={14}
        />

        {apto !== null && (
          <div className={`mensagem-validacao ${apto ? 'apto' : 'nao-apto'}`}>
            {apto ? "✅ Apto para votar!" : "❌ Não está apto para votar"}
          </div>
        )}

        {apto && (
          <>
            <h2>Pautas Disponíveis:</h2>
            <div className="pauta-container">
              {pautas.map(pauta => (
                <PautaItem key={pauta.id} pauta={pauta} setVotos={setVotos} />
              ))}
            </div>

            <h2>Votos Registrados:</h2>
            {votos.map((voto, index) => (
              <div key={index} className="pauta-item">
                <strong>{voto.titulo}</strong> → {voto.opcao === 'sim' ? '✅ Sim' : '❌ Não'}
              </div>
            ))}
          </>
        )}
    </div>
  );
};

export default Home;
